OpenGL.Vertex:
#version 330 core

layout(location = 0) in vec2 a_Position;
uniform mat4 proj;

void main()
{
    gl_Position =  vec4(a_Position, 0.0f, 1.0f) * proj;
}

OpenGL.Pixel:
#version 330 core

layout(location = 0) out vec4 color;
uniform vec3 clientColor;

void main()
{
    color = vec4(clientColor, 1.0f);
}
