    outColor += texture(inputTexture, uv + vec2(deltaX * 19, deltaY * 14)) * 1.1046788188690944e-05f;
    outColor += texture(inputTexture, uv + vec2(deltaX * 19, deltaY * 15)) * 8.217132933733782e-06f;
    outColor += texture(inputTexture, uv + vec2(deltaX * 19, deltaY * 16)) * 5.9888225654177174e-06f;
    outColor += texture(inputTexture, uv + vec2(deltaX * 19, deltaY * 17)) * 4.276607829804944e-06f;
    outColor += texture(inputTexture, uv + vec2(deltaX * 19, deltaY * 18)) * 2.9922250516379757e-06f;
    outColor += texture(inputTexture, uv + vec2(deltaX * 19, deltaY * 19)) * 2.0512846484622733e-06f;
    outColor *= 500.0f * power/100.0f;
    vec3 hsv = rgb2hsv(outColor.rgb);
    outColor.a = hsv.b;
}
